        String text = "";
        if (mTextIOText != null)
            text = mTextIOText.getText().toString();

        int id = item.getItemId();
        if (id == android.R.id.home) {
            confirm();
        } if (id == R.id.menu_item_cancel) {
            cancel();
        } else if (id == R.id.menu_item_share_text) {
            ShareUtils.shareText(this, mTextIOInfo.getTitle(), text);
